def prot(rna):
	#Create a codon dictionary
	base = ['u', 'c', 'a', 'g']
	codontable = [a+b+c for a in base for b in base for c in base]
	aminoacids = 'FFLLSSSSYY**CC*WLLLLPPPPHHQQRRRRIIIMTTTTNNKKSSRRVVVVAAAADDEEGGGG'
	codond = dict(zip(codontable, aminoacids))
	
	#Create list of codons in RNA
	rna = rna.lower()
	codonfrags = [rna[i:i+3] for i in range(0, len(rna),3)]
	
	#Translate RNA to protein
	protein = ""
	for codon in codonfrags:
		protein += codond[codon]
	return protein[:-1]

#Test
print (prot(""))